import { Component, Input, OnInit, SimpleChanges, TemplateRef, ViewChild } from '@angular/core';
import { FormArray, FormBuilder, FormControl, FormGroup, FormGroupDirective } from '@angular/forms';
import { DomSanitizer } from '@angular/platform-browser';
import { BsModalRef, BsModalService } from 'ngx-bootstrap/modal';
import { CompanyServiceService } from 'src/app/views/app/customer-portal/customer-company/company-service.service';
import {Attachement} from '../../../data/Customer';
import { AddNewAttachmentComponent } from '../../add-new-modal/add-new-attachment/add-new-attachment.component';

@Component({
  selector: 'app-company-attachment',
  templateUrl: './company-attachment.component.html'
})
export class CompanyAttachmentComponent implements OnInit {

  attachmentCompanyForm:FormGroup;

  modalRef: BsModalRef;
  message: string;

  @Input() formArrayName:string;
  @Input() attachementData: Attachement[];

  @ViewChild('addNewAttachmentModalRef', { static: true }) addNewAttachmentModalRef:AddNewAttachmentComponent;
  @Input() custObjectID:any;


  fileUrl;


  constructor(private fb:FormBuilder,
              private rootFormGroup:FormGroupDirective,
              private companyAccService: CompanyServiceService,
              private modalService: BsModalService,
              private sanitizer: DomSanitizer) { }

  ngOnInit() {
  }
  ngOnChanges(changes: SimpleChanges) {
    this.attachmentCompanyForm=this.rootFormGroup.control;
    if (changes['attachementData'] && this.attachementData)
    {
      this.attachementData.forEach((item:Attachement)=>{
      let resource = this.createtablerow();
      resource.patchValue({
         title: item.title,
         attachmentDocument: item.attachmentDoc
        });
      this.attachmentFormArray.push(resource);
    });
    }
    (<FormArray>this.attachmentCompanyForm.get('attachmentCompanyFormArray')).controls.forEach(control=>{
      control.disable();
    });

  }
  showAddNewModal(): void {
    this.addNewAttachmentModalRef.showModal();
  }
  onSubmit(){
    if (this.attachmentCompanyForm.valid) {
      console.log("Form Submitted!",this.attachmentCompanyForm.value);
    }
  }
  createtablerow():FormGroup{
    return this.fb.group({
      title: new FormControl(''),
      attachmentDocument: new FormControl(''),
  });
  }
  get attachmentFormArray(): FormArray {
    return this.attachmentCompanyForm.get(this.formArrayName) as FormArray;
  }
  addAttachment(attachement:Attachement) {
    this.attachementData=[...this.attachementData,attachement];
    console.log("add",this.attachementData)
      let resource = this.createtablerow();
      resource.patchValue({
         title: attachement.title,
         attachmentDocument: attachement.attachmentDoc
        });
      this.attachmentFormArray.push(resource);
    (<FormArray>this.attachmentCompanyForm.get('attachmentCompanyFormArray')).controls.forEach(control=>{
      control.disable();
    });
  }
  onDeleteRow(template: TemplateRef<any>): void {
    this.modalRef = this.modalService.show(template, { class: 'modal-sm' });
  }
  confirm(rowIndex: number):void{
    this.attachmentFormArray.removeAt(rowIndex);
    let id=this.attachementData[rowIndex].objectID
    this.companyAccService.deleteAttachement(id).subscribe()
    this.modalRef.hide();
  }

  decline(): void {
    this.modalRef.hide();
  }

  showPdf(rowIndex:number) {
    console.log(this.attachementData[rowIndex].type)
    console.log("index",rowIndex)
    console.log(this.attachementData)
    const linkSource =
      'data:'+this.attachementData[rowIndex].type+';base64,' +this.attachementData[rowIndex].attachmentDoc
      //'';
    const downloadLink = document.createElement('a');
    let fileName = this.attachementData[rowIndex].title//'sample.pdf';
    //console.log(linkSource)
    downloadLink.href = linkSource;
    downloadLink.download = fileName.toString();
    downloadLink.click();
  }

}
