import { Component, Input, OnInit, SimpleChanges, TemplateRef, ViewChild } from '@angular/core';
import { FormArray, FormBuilder, FormControl, FormGroup, FormGroupDirective } from '@angular/forms';
import { BsModalRef, BsModalService } from 'ngx-bootstrap/modal';
import { IndividualServiceService } from 'src/app/views/app/customer-portal/customer-individual/individual-service.service';
import { Attachement } from '../../../data/Customer';
import { AddIndividualAttachmentComponent } from '../../add-new-modal/add-individual-attachment/add-individual-attachment.component';

@Component({
  selector: 'app-profile-attachment',
  templateUrl: './profile-attachment.component.html'
})
export class ProfileAttachmentComponent implements OnInit {

  attachmentform: FormGroup;
  @Input() generalData: any;
  @Input() formArrayName: string;
  @Input() custObjectID:any;
  @ViewChild('addNewAttachmentModalRef', { static: true }) addNewAttachmentModalRef: AddIndividualAttachmentComponent;

  modalRef: BsModalRef;

  constructor(private fb: FormBuilder,
    private rootFormGroup: FormGroupDirective,
    private modalService: BsModalService,
    private individualAccService:IndividualServiceService) { }

  ngOnChanges(changes: SimpleChanges) {
    this.attachmentform = this.rootFormGroup.control;
    // only run when property "data" changed
    if (changes['generalData'] && this.generalData) {
      this.generalData.attachement.forEach((item: Attachement) => {
        let resource = this.createtablerow();
        resource.patchValue({
          title: item.title,
          attachmentdocument: item.attachmentDoc
        });
        this.attachmentFormArray.push(resource);
      })
    }
    (<FormArray>this.attachmentform.get(this.formArrayName))
      .controls
      .forEach(control => {
        control.disable();
      });
  }

  showAddNewModal(): void {
    this.addNewAttachmentModalRef.showModal();
  }
  ngOnInit() {
  }
  createtablerow(): FormGroup {
    return this.fb.group({
      title: new FormControl(''),
      attachmentdocument: new FormControl(''),
    });
  }
  get attachmentFormArray(): FormArray {
    return this.attachmentform.get(this.formArrayName) as FormArray;
  }
  addAttachment(attachement:Attachement){
    this.generalData.attachement=[...this.generalData.attachement,attachement];
    console.log("add",this.generalData.attachement);
      let resource = this.createtablerow();
      resource.patchValue({
         title: attachement.title,
         attachmentDocument: attachement.attachmentDoc
        });
      this.attachmentFormArray.push(resource);
      (<FormArray>this.attachmentform.get(this.formArrayName)).controls.forEach(control => {
        control.disable();
      });
  }
  onDeleteRow(template: TemplateRef<any>): void {
    this.modalRef = this.modalService.show(template, { class: 'modal-sm' });
  }
  confirm(rowIndex: number): void {
    this.attachmentFormArray.removeAt(rowIndex);
    let id=this.generalData.attachement[rowIndex].objectID;
    this.individualAccService.deleteAttachement(id).subscribe()
    this.modalRef.hide();
  }

  decline(): void {
    this.modalRef.hide();
  }
  onUploadError(event): void {
    console.log(event);
  }

  onUploadSuccess(event): void {
    console.log(event);
  }
  showPdf(rowIndex:number) {
    console.log(this.generalData.attachement[rowIndex].type)
    console.log("index",rowIndex)
    console.log(this.generalData.attachement)
    const linkSource =
      'data:'+this.generalData.attachement[rowIndex].type+';base64,' +this.generalData.attachement[rowIndex].attachmentDoc
      //'';
    const downloadLink = document.createElement('a');
    let fileName = this.generalData.attachement[rowIndex].title//'sample.pdf';
    //console.log(linkSource)
    downloadLink.href = linkSource;
    downloadLink.download = fileName.toString();
    downloadLink.click();
  }

}
